    translate.setDefaultLang('en');
    const browserLang = translate.getBrowserLang();
    translate.use(browserLang.match(/en|fr/) ? browserLang : 'en');
    this.getState = this.store.select(selectAuthState);
  }

  ngOnInit(): void {
    this.getResponseData();
  }

  getResponseData() {
