  columns: any =
    [
      { key: 'HEADER_TEXT_1' , title:'title'},
      { key: 'HEADER_TEXT_2' , title:'author'},
      { key: 'HEADER_TEXT_3' , title:'website'},
      { key: 'HEADER_TEXT_4', title: 'publisher' },
    ];

  editFormData: any = {};
  openFormModal: boolean = false;
  bookData: any;

  deleteForm: boolean = false;
  deleteId: string;
  constructor(
    private bookService: BookService,
    private toastrService: ToastrService,
    private apiService: ApiServiceService
  ) {
  }

  ngOnInit(): void {
    this.getBookData();
  }

  getBookData(): void {
    let responseData = [];
    const userToken = this.apiService.getToken();
    const emailId = userToken.email;
    console.log('emailId', emailId);
    this.bookService.getBook().subscribe((data) => {
      responseData = data;
      this.bookData = responseData.filter(obj => obj.createdBy === emailId);
    });
  }

  onEdit(id: string): void {
    this.bookService.getBookById(id).subscribe((data) => {
      this.editFormData = data;
      this.openFormModal = true;
    });
  }

  onDelete(id: string): void {
    this.deleteForm = true;
    this.deleteId = id;
  }

  deleteBook(): void {
    this.bookService.deleteBookById(this.deleteId).subscribe(() => {
      this.deleteForm = false;
      this.deleteId = '';
      this.toastrService.info('Deleted Successfully');
      this.getBookData();
    });
  }

  addBook(): void {
    this.openFormModal = true;
  }

  closeFormModal(): void {
    this.openFormModal = false;
    this.editFormData = {};
  }

  closeModal(): void {
    this.deleteForm = false;
    this.deleteId = '';
  }

  getData(): void {
    this.getBookData();
  }
}

